(function () {
    'use strict';

    // import GameControl from "./GameControl"
    class GameUI extends Laya.Scene {
        constructor() {
            super();
            //设置单例的引用方式，方便其他类引用
            GameUI.instance = this;
            //关闭多点触控，否则就无敌了
            Laya.MouseManager.multiTouchEnabled = false;
            //加载场景文件
            this.loadScene("gameBox.scene");
            this.alpha = 0; //过渡页面
            this._UItransition(null, this, this.showShine);
            this.initPage();
        }

        onEnable() {
            this.touchArrHandle();
        }

        onTipClick(e) {

        }
        /** 初始化页面*/
        initPage() {
            //初始话页面
            // this.bg.height = Laya.stage.height
            let bottomHeight = this.getChildByName('bottom').height;
            this.getChildByName('bottom').y = Laya.stage.height - bottomHeight;
            let ballItem = this.getChildByName('ball');
            ballItem.y = Laya.stage.height - bottomHeight - ballItem.height - 15;
        }
        /**增加分数 */
        addScore(value) {

        }

        /**停止游戏 */
        stopGame() {

        }
        /**
         * 页面过渡
         */
        _UItransition(UIa, UIb, callback) {
            // Laya.Tween.to(UIa, {
            //     alpha: 0
            // }, 500);

            UIb.visible = true;
            Laya.Tween.to(UIb, {
                alpha: 1
            }, 500);

            setTimeout(() => {
                // UIa.visible = false;
                if (callback) callback();
            }, 500);
        }

        /**显示瓶子亮光 */
        showShine() {
            // _this.bottleBox.getChildByName('shine1').play()
        }
        /**
         * 手指按住屏幕旋转箭头
         */
        touchArrHandle() {
            this.on(Laya.Event.MOUSE_DOWN, this, this.onMouseDown);
            Laya.stage.on(Laya.Event.MOUSE_UP, this, this.onMouseUp);
            Laya.stage.on(Laya.Event.MOUSE_OUT, this, this.onMouseUp);
        }

        onMouseDown(e) {
            const Event = Laya.Event;
            // 手机上才有 touches 属性
            let touches = e.touches;
            this.arrBox.rotation =14;
            if (touches && touches.length == 2) {
                preRadian = Math.atan2(
                    touches[0].stageY - touches[1].stageY,
                    touches[0].stageX - touches[1].stageX);

                Laya.stage.on(Laya.Event.MOUSE_MOVE, this, this.onMouseMove);
            }
        }

        onMouseMove(e) {
            let touches = e.touches;
            if (touches && touches.length == 2) {
                let nowRadian = Math.atan2(
                    touches[0].stageY - touches[1].stageY,
                    touches[0].stageX - touches[1].stageX);

                this.bottleBox.getChildByName('arrBox').rotation += 180 / Math.PI * (nowRadian - preRadian);

                preRadian = nowRadian;
            }
        }

        onMouseUp(e) {
            const Event = Laya.Event;
            Laya.stage.off(Laya.Event.MOUSE_MOVE, this, this.onMouseMove);
        }
    }

    /**This class is automatically generated by LayaAirIDE, please do not make any modifications. */

    class GameConfig {
        static init() {
            //注册Script或者Runtime引用
            let reg = Laya.ClassUtils.regClass;
    		reg("script/GameUI.js",GameUI);
        }
    }
    GameConfig.width = 750;
    GameConfig.height = 1624;
    GameConfig.scaleMode ="fixedwidth";
    GameConfig.screenMode = "none";
    GameConfig.alignV = "middle";
    GameConfig.alignH = "center";
    GameConfig.startScene = "gameBox.scene";
    GameConfig.sceneRoot = "";
    GameConfig.debug = false;
    GameConfig.stat = false;
    GameConfig.physicsDebug = false;
    GameConfig.exportSceneToJson = true;

    GameConfig.init();

    var Loader = Laya.Loader;
    var PreResources = [
    	{ url: 'gameBox/bg.jpg', type: Loader.IMAGE },
    	{ url: 'gameBox/bottle.png', type: Loader.IMAGE },
    	{ url: 'gameBox/bottom.png', type: Loader.IMAGE },
    	{ url: 'res/atlas/gameBox.atlas', type: Loader.ATLAS },
    	{ url: 'res/atlas/gameBox.png', type: Loader.IMAGE }
    ];
    class Main {
    	constructor() {
    		//根据IDE设置初始化引擎		
    		if (window["Laya3D"]) Laya3D.init(GameConfig.width, GameConfig.height);
    		else Laya.init(GameConfig.width, GameConfig.height, Laya["WebGL"]);
    		Laya["Physics"] && Laya["Physics"].enable();
    		Laya["DebugPanel"] && Laya["DebugPanel"].enable();
    		Laya.stage.scaleMode = GameConfig.scaleMode;
    		Laya.stage.screenMode = GameConfig.screenMode;
    		Laya.stage.alignV = GameConfig.alignV;
    		Laya.stage.alignH = GameConfig.alignH;
    		Laya.stage.bgColor = "none";
    		//兼容微信不支持加载scene后缀场景
    		Laya.URL.exportSceneToJson = GameConfig.exportSceneToJson;

    		//打开调试面板（通过IDE设置调试模式，或者url地址增加debug=true参数，均可打开调试面板）
    		if (GameConfig.debug || Laya.Utils.getQueryString("debug") == "true") Laya.enableDebugPanel();
    		if (GameConfig.physicsDebug && Laya["PhysicsDebugDraw"]) Laya["PhysicsDebugDraw"].enable();
    		if (GameConfig.stat) Laya.Stat.show();
    		Laya.alertGlobalError = true;

    		//激活资源版本控制，version.json由IDE发布功能自动生成，如果没有也不影响后续流程
    		Laya.ResourceVersion.enable("version.json", Laya.Handler.create(this, this.onVersionLoaded), Laya.ResourceVersion.FILENAME_VERSION);
    	}

    	onVersionLoaded() {
    		//激活大小图映射，加载小图的时候，如果发现小图在大图合集里面，则优先加载大图合集，而不是小图
    		Laya.AtlasInfoManager.enable("fileconfig.json", Laya.Handler.create(this, this.onConfigLoaded));		
    	}

    	onConfigLoaded() {		
    		//加载IDE指定的场景
    		GameConfig.startScene && Laya.Scene.open(GameConfig.startScene);
    	}
    }
    //激活启动类
    new Main();

}());
